mapper.select:
SELECT quality_id, user_id, org_id, score, start_time, end_time, used_time, create_time
FROM train_quality


        AND user_id = #{userId}


        AND org_id = #{orgId}


ORDER BY create_time DESC

SELECT quality_id, user_id, org_id, score, start_time, end_time, used_time, create_time
FROM train_quality
WHERE quality_id = #{qualityId}
